export const addItemsToCart = (cartItems, ...itemsToAdd) => {
  for (const item of itemsToAdd) {
    const existingCartItem = cartItems.findIndex(
      cartItem => cartItem.id === item.id
    );

    if (existingCartItem > -1) {
      cartItems[existingCartItem] = {
        ...cartItems[existingCartItem], 
        quantity: cartItems[existingCartItem].quantity + item.quantity
      };
    } else {
      cartItems.push(item);
    }

    return cartItems;
  }
}
