import styled from 'styled-components';

export const ContactPage = styled.div`
  width: 380px;
  display: flex;
  flex-direction: column;

  @media screen and (max-width: 800px) {
    margin-bottom: 50px;
  }
`;

export const Title = styled.h1`
  margin: 10px 0;  
`;

export const MessageText = styled.textarea`
  height: 150px;
  width: 500px;
  margin: 10px 0 25px;
  border-radius: 5px;
`;
