import {createSelector} from 'reselect';

const selectCartItems = state => state.cart.cartItems;

export const selectCartItemCount = createSelector(
  [selectCartItems],
  cartItems => 
    cartItems.reduce(
      ((total, cartItem) => total + cartItem.quantity),
    0)
);
