import React from 'react';
import * as S from './loading-spinner-styles';


const LoadingSpinner = WrappedComponent => {
  const Spinner = ({isLoading, ...rest}) => {
    return (
      isLoading ? 
      <S.SpinnerOverlay>
        <S.Spinner />
      </S.SpinnerOverlay>
      : <WrappedComponent {...rest} />
    );
  };
  return Spinner;
};

export default LoadingSpinner;
