import React from 'react';
import * as S from './contact-page-styles';

import axios from 'axios';

import CustomButton from '../../components/custom-button/custom-button';


const ContactPage = () => {

  const onSubmit = event => {
    axios({
      url: 'contact',
      method: 'post',
      data: event.target.value
    })
  };

  return (
    <S.ContactPage>
      <S.Title>Questions? Comments? Let us know!</S.Title>
      <S.MessageText required className='message-text' placeholder='Your message here' />
      <S.Button as={CustomButton} type='button' onClick={onSubmit}>
        Submit
      </S.Button>
    </S.ContactPage>
  );
};

export default ContactPage
